import React, { useState } from 'react';
import { SafeAreaView, View, VirtualizedList, StyleSheet, Text, StatusBar, Image } from 'react-native';
import * as data from '../MoviesList.json';

const DATA = data.Search
console.log(DATA)

const getItem = (data, index) => {
    let a = data[index].Poster; 
    return ({
    id: Math.random().toString(12).substring(0),
    title: `${data[index].Title}`,
    year: `${data[index].Year}`,
    type: `${data[index].Type}`,
    poster: data[index].Poster != '' ? require('../posters/' + a) : require('../posters/noposter.png')
     
})};

const getItemCount = (data) => data.length;


const Item = ({ title, year, type, poster }) => (
    <View style={styles.item}>
        <View style={styles.posterViev}>
            <Image
                style={styles.poster}
                source={poster}
                // source={require('../posters/Poster_07.jpg') }
            />
        </View>
        <View style={styles.textViev}>
            <Text style={styles.title}>{title}</Text>
            <Text style={styles.specs}>{year}</Text>
            <Text style={styles.specs}>{type}</Text>
        </View>
    </View>
);

const MoviesScreen = () => {
    return (
        <SafeAreaView style={styles.container}>
            <VirtualizedList
                data={DATA}
                initialNumToRender={4}

                renderItem={({ item }) => 
                    <Item title={item.title} year={item.year} type={item.type} poster={item.poster} />}
                
                keyExtractor={(item, index) => {
                    return item.id;
                }}
                getItemCount={getItemCount}
                getItem={getItem}
            />
        </SafeAreaView>
    );
}

const styles = StyleSheet.create({
    container: {
        flex: 1,
        marginTop: StatusBar.currentHeight,
        backgroundColor: '#fff',
    },
    item: {
        flexDirection: 'row',

        marginBottom: 1,
        borderBottomColor: 'grey',
        borderBottomWidth: 1,
        
        backgroundColor: 'white',
        height: 150,
        justifyContent: 'center',
        marginVertical: 8,
        marginHorizontal: 16,
        padding: 20,
    },
    'item:last-child': {
        borderBottomWidth: 0
    },
    title: {
        fontSize: 12,
    },
    poster: {
        flex: 1,
        width: 50,
        height: 50,
    },

    posterViev: {
        flex: 2
    },

    textViev: {
        flex: 10
    },

    specs: {
        marginTop: 10,
        fontSize: 12,
    }
});

export default MoviesScreen;
