import React from 'react';
import { SafeAreaView, View, VirtualizedList, StyleSheet, Text, StatusBar, Image } from 'react-native';
import * as data from '../MoviesList.json';

const DATA = data.Search

// let key = 'Poster_01.jpg'

const posterSelector = (key) => {
    switch (key) {
        case 'Poster_01.jpg':
            return require('../posters/Poster_01.jpg')
        case 'Poster_02.jpg':
            return require('../posters/Poster_02.jpg')
        case 'Poster_03.jpg':
            return require('../posters/Poster_03.jpg')
        case 'Poster_05.jpg':
            return require('../posters/Poster_05.jpg')
        case 'Poster_06.jpg':
            return require('../posters/Poster_06.jpg')
        case 'Poster_07.jpg':
            return require('../posters/Poster_07.jpg')
        case 'Poster_08.jpg':
            return require('../posters/Poster_08.jpg')
        case 'Poster_10.jpg':
            return require('../posters/Poster_10.jpg')
            
        default:
            return require('../posters/noposter.png')
    }
}


const getItem = (data, index) => {
    return ({
        id: Math.random().toString(12).substring(0),
        title: `${data[index].Title}`,
        year: `${data[index].Year}`,
        type: `${data[index].Type}`, 
        poster: `${data[index].Poster}`
})};

const getItemCount = (data) => data.length;


const Item = ({ title, year, type, poster }) => (
    <View style={styles.item}>
        <View style={styles.posterViev}>
            <Image
                style={styles.poster}
                source={posterSelector(poster)}
            />
        </View>
        <View style={styles.textViev}>
            <Text style={styles.title}>{title}</Text>
            <Text style={styles.specs}>{year}</Text>
            <Text style={styles.specs}>{type}</Text>
        </View>
    </View>
);

const MoviesScreen = () => {
    return (
        <SafeAreaView style={styles.container}>
            <VirtualizedList
                data={DATA}
                initialNumToRender={4}

                renderItem={({ item }) => 
                    <Item title={item.title} year={item.year} type={item.type} poster={item.poster}/>}
                
                keyExtractor={(item, index) => {
                    return item.id;
                }}
                getItemCount={getItemCount}
                getItem={getItem}
            />
        </SafeAreaView>
    );
}

const styles = StyleSheet.create({
    container: {
        flex: 1,
        marginTop: StatusBar.currentHeight,
        backgroundColor: '#fff',
    },
    item: {
        flexDirection: 'row',
        marginBottom: 1,
        backgroundColor: 'white',
        height: 'auto',
        justifyContent: 'center',
        marginVertical: 8,
        marginHorizontal: 0,
        padding: 20,
        
    },
    title: {
        fontSize: 12,
        marginLeft: 5
    },
    poster: {
        flex: 1,
        width: 60,
        height: 100,
        
    },

    posterViev: {
        flex: 2,
        marginLeft: 5
    },

    textViev: {
        flex: 10
    },

    specs: {
        marginTop: 10,
        fontSize: 12,
    }
});

export default MoviesScreen;
