import React, {useState} from 'react';
import { Text, View, StyleSheet } from 'react-native';
import SwitchSelector from 'react-native-switch-selector';
import { LineChart, PieChart} from "react-native-chart-kit";
import { Dimensions } from "react-native";

const screenWidth = Dimensions.get("window").width;

const options = [
    { label: 'Графік', value: true },
    { label: 'Діаграма', value: false },
];





function ChartShow(props) {

    const [isEnabled, setIsEnabled] = useState(false);
    const toggleSwitch = () => setIsEnabled(previousState => !previousState);

    let isSwitched = props.isSwitched
    if (isEnabled) {
        return (
            <View>
                <Text>Hello</Text>
            
                <LineChart
                    withVerticalLines={false}
                    withInnerLines={false}
                    withOuterLines={false}
                    data={data}
                    width={screenWidth}
                    chartConfig={chartConfig}
                    height={300}
                    bezier
                    style={{
                        marginVertical: 15,
                        paddingLeft: 55

                    }}
                />
            </View>
        )
    } 

    if (!isEnabled) {
        return (
            <Text>Hello</Text>
        )
    }
    // else {
    //     return (
    //         <Text>Hello</Text>
    //     )
}


const data = {
    fromZero: true,
    datasets: [
        {
            
            data: [4**2, 3**2, 4**2],
            color: (opacity = 1) => `rgba(0, 0, 0, ${opacity})`, // optional
            strokeWidth: 2
        }
    ],
    
};

const chartConfig = {
    backgroundGradientFrom: "white",
    backgroundGradientTo: "white",
    color: (opacity = 1) => `rgba(255, 255, 255, ${opacity})`,
    
    propsForDots: {
        r: "1",
        strokeWidth: "2",
    }
};

let a = true

export default function DrawingScreen() {
    return (
        <View style={styles.container}>

            <SwitchSelector
                // style={{ paddingBottom: 50, marginBottom: 100, zIndex: 100, position: 'absolute', backgroundColor: 'white' }}
                options={options}
                initial={1}
                fontSize={12}
                textColor={"#aaaaaa"}
                selectedColor={'#fff'}
                buttonColor={'#262625'}
                backgroundColor={"#EEEEEE"}
                borderColor={"#353535"}
                borderRadius={50}
                onPress={toggleSwitch}
            />
            
            <ChartShow isSwitched={true} />
            
        </View>
    );
}


const styles = StyleSheet.create({
    container: {
        flex: 1,
        alignItems: 'center',
        justifyContent: 'center',
        paddingLeft: 50,
        paddingRight: 50,
        backgroundColor: '#FFF'
    },
    
});
